mapper.select:
SELECT rno,name,price,fno,rstate,id,inwon,msg,TO_CHAR(sdate, 'YYYY-MM-DD') as dbsdate,TO_CHAR(edate, 'YYYY-MM-DD') as dbedate,image,campsite_name,camp_name,num FROM (SELECT rno,name,price,fno,rstate,id,inwon,msg,sdate,edate,image,campsite_name,camp_name,rownum as num FROM (SELECT rno,name,price,fno,rstate,id,inwon,msg,sdate,edate,(SELECT image FROM camp2 WHERE camp2.cno=reserve2.fno) as image, (SELECT name FROM campsite2 WHERE campsite2.csno=reserve2.csno) as campsite_name,(SELECT name FROM camp2 WHERE camp2.cno=reserve2.fno) as camp_name FROM reserve2 WHERE type='c'
AND rstate=#{rstate}
ORDER BY rno DESC)) WHERE num BETWEEN #{start} AND #{end}
