mapper.select:
SELECT cno,name,address,phone,msg,image,(SELECT MIN(TO_NUMBER(price)) FROM campsite2 cs WHERE cs.cno=c.cno) as mprice,num FROM (SELECT cno,name,address,phone,msg,image,rownum as num FROM (SELECT cno,name,address,phone,msg,image FROM camp2 WHERE (SELECT COUNT(sDate) FROM reserve2 WHERE camp2.rno=reserve2.cno AND sDate BETWEEN #{sDate} AND #{eDate} AND type='c') = 0 AND (SELECT COUNT(eDate) FROM reserve2 WHERE camp2.rno=reserve2.cno AND eDate BETWEEN #{sDate} AND #{eDate} AND type='c') = 0 AND (SELECT MIN(TO_NUMBER(price)) FROM campsite2 cs WHERE cs.cno=c.cno) BETWEEN #{spricefd} AND #{epricefd}
AND address LIKE '%'||#{state}||'%'
AND name LIKE '%'||#{campfd}||'%'
ORDER BY cno ASC)) WHERE num BETWEEN #{start} AND #{end}
